function horizontalScrollToNextTestimonial(){testimonials.scrollBy({left:testimonials.offsetWidth,behavior:"smooth"})}function horizontalScrollToPreviousTestimonial(){testimonials.scrollBy({left:-testimonials.offsetWidth,behavior:"smooth"})}const toggleMenuButtons=document.querySelectorAll("button[data-target='toggle-menu']");toggleMenuButtons.forEach(function(e){e.addEventListener("click",function(){document.getElementById("menu-mobile").classList.toggle("hidden")})});const menuLinks=document.querySelectorAll("#menu-mobile a");menuLinks.forEach(function(e){e.addEventListener("click",function(){document.getElementById("menu-mobile").classList.add("hidden")})});const menu=document.getElementById("menu");document.addEventListener("scroll",function(){window.scrollY>0?menu.classList.add("bg-teal-50"):menu.classList.remove("bg-teal-50")});const anchorLinks=document.querySelectorAll("a[href^='#']");anchorLinks.forEach(function(e){e.addEventListener("click",function(t){t.preventDefault(),document.querySelector(e.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});const testimonials=document.querySelector("[data-target='testimonials']");document.getElementById("next-testimonial").addEventListener("click",horizontalScrollToNextTestimonial),document.getElementById("previous-testimonial").addEventListener("click",horizontalScrollToPreviousTestimonial),window.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("a[href^='https://calendly.com/']").forEach(function(e){e.addEventListener("click",function(t){t.preventDefault();const n=e.getAttribute("href"),o=n.split("/").pop();window.beam("/calendly/"+o).then(function(){window.location.href=n})})})});